import Axios from "axios";
import "./App.css";
import { useState } from "react";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import Dashboard from "./components/Dashboard";
import Login from "./components/Login";
import Navbar from "./components/Navbar";
import Register from "./components/Register";

function App() {
  const [usernameReg, setUserNameReg] = useState("");
  const [passwordReg, setPasswordReg] = useState("");
  
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const [loginStatus, setLoginStatus] = useState("");

  const [name, setName] = useState("");
  const [age, setAge] = useState(0);
  const [country, setCountry] = useState("");
  const [position, setPosition] = useState("");
  const [wage, setWage] = useState(0);

  const [newWage, setNewWage] = useState(0);

  const [employeeList, setEmployeeList] = useState([]);

  const [sorted, setSorted] = useState(false);

  // const register = () => {
  //   Axios.post("http://localhost:3001/register", {
  //     username: usernameReg,
  //     password: passwordReg,
  //   }).then((Response) => {
  //     console.log(Response);
  //   });
  // };

  // const login = () => {
  //   Axios.post("http://localhost:3001/login", {
  //     username: username,
  //     password: password,
  //   }).then((Response) => {

  //     if (Response.data.message) {
  //       setLoginStatus(Response.data.message)
  //     } else {
  //       setLoginStatus(Response.data[0].username)
  //     }
  //   });
  // }

  const addEmployee = () => {
    Axios.post("http://localhost:5000/create", {
      name: name,
      age: age,
      country: country,
      position: position,
      wage: wage,
    }).then(() => {
      getEmployees();
    });
  };

  const toggleSort = (sorted) => {
    Axios.get("http://localhost:3001/employeesSorted?order=" + sorted).then(
      (response) => {
        setEmployeeList(response.data);
        setSorted(!sorted);
        console.log(response);
      }
    );
  };

  const getEmployees = () => {
    Axios.get("http://localhost:5000/employees").then((response) => {
      setEmployeeList(response.data);
    });
  };

  const updateEmployeeWage = (id) => {
    Axios.put("http://localhost:3001/update", { wage: newWage, id: id }).then(
      () => {
        // getEmployees();
        setEmployeeList(
          employeeList.map((val) => {
            return val.id == id
              ? {
                  id: val.id,
                  name: val.name,
                  country: val.country,
                  age: val.age,
                  position: val.position,
                  wage: newWage,
                }
              : val;
          })
        );
      }
    );
  };

  const deleteEmployee = (id) => {
    Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
      setEmployeeList(
        employeeList.filter((val) => {
          return val.id != id;
        })
      );
    });
  };

  return (
    <div>
      <BrowserRouter>
        <Switch>
          <Route exact path="/">
            <Login />
          </Route>
          <Route path="/register">
            <Register />
          </Route>
          <Route path="/dashboard">
            <Navbar />
            <Dashboard />
          </Route>
        </Switch>
      </BrowserRouter>

      <div className="App">
        <div className="information">
          <label>Name:</label>
          <input
            type="text"
            onChange={(event) => {
              setName(event.target.value);
            }}
          />
          <label>Age:</label>
          <input
            type="number"
            onChange={(event) => {
              setAge(event.target.value);
            }}
          />
          <label>Country:</label>
          <input
            type="text"
            onChange={(event) => {
              setCountry(event.target.value);
            }}
          />
          <label>Position:</label>
          <input
            type="text"
            onChange={(event) => {
              setPosition(event.target.value);
            }}
          />
          <label>Wage (year):</label>
          <input
            type="number"
            onChange={(event) => {
              setWage(event.target.value);
            }}
          />
          <button onClick={addEmployee}>Add Employee</button>
        </div>
        <hr />
        <div className="employees">
          <button onClick={getEmployees}>Show Employees</button>

          <button
            className="toggle-button"
            onClick={() => {
              toggleSort(sorted);
            }}
          >
            {sorted ? "Ascending Order ▲" : "Descending Order ▼"}
          </button>

          {employeeList.map((val, key) => {
            return (
              <div className="employee">
                <div>
                  <h3>Name: {val.name}</h3>
                  <h3>Age: {val.age}</h3>
                  <h3>Country: {val.country}</h3>
                  <h3>Position: {val.position}</h3>
                  <h3>Wage: {val.wage}</h3>
                </div>
                <div>
                  <input
                    type="text"
                    placeholder="20000..."
                    onChange={(event) => {
                      setNewWage(event.target.value);
                    }}
                  />
                  <button onClick={() => updateEmployeeWage(val.id)}>
                    Update
                  </button>

                  <button onClick={() => deleteEmployee(val.id)}>Delete</button>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
